import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Calculator, Heart, Activity, Plus, Eye, MessageCircle } from 'lucide-react';
import { nutritionAPI } from '../lib/api';
import { useToast } from '../components/ui/use-toast';
import useAuthStore from '../store/authStore';

const NutritionPlan = () => {
  const { isDoctor } = useAuthStore();
  const { toast } = useToast();

  const [calculatorData, setCalculatorData] = useState({
    age: '',
    weight: '',
    height: '',
    gender: 'male',
    activity_level: 'moderate',
    goal: 'maintain',
    diseases: [],
  });

  const [showDemo, setShowDemo] = useState(false);

  // Fetch diseases
  const { data: diseases } = useQuery({
    queryKey: ['diseases'],
    queryFn: nutritionAPI.getDiseases,
  });

  // Fetch nutrition plans
  const { data: nutritionPlans, refetch: refetchPlans } = useQuery({
    queryKey: ['nutrition-plans'],
    queryFn: nutritionAPI.getPlans,
  });

  // Fetch demo data
  const { data: demoData } = useQuery({
    queryKey: ['nutrition-demo'],
    queryFn: nutritionAPI.getDemo,
    enabled: showDemo,
  });

  // Fetch WhatsApp messages
  const { data: whatsappMessages } = useQuery({
    queryKey: ['whatsapp-messages'],
    queryFn: nutritionAPI.getWhatsAppMessages,
  });

  // Calculate calories mutation
  const calculateMutation = useMutation({
    mutationFn: nutritionAPI.calculateCalories,
    onSuccess: (response) => {
      toast({
        title: "Calculation complete",
        description: `Recommended daily calories: ${response.data.recommended_calories}`,
      });
    },
    onError: (error) => {
      toast({
        title: "Calculation failed",
        description: error.response?.data?.detail || "Failed to calculate calories",
        variant: "destructive",
      });
    },
  });

  // Create plan mutation
  const createPlanMutation = useMutation({
    mutationFn: nutritionAPI.createPlan,
    onSuccess: () => {
      toast({
        title: "Plan created",
        description: "Nutrition plan created successfully",
      });
      refetchPlans();
      setCalculatorData({
        age: '',
        weight: '',
        height: '',
        gender: 'male',
        activity_level: 'moderate',
        goal: 'maintain',
        diseases: [],
      });
    },
    onError: (error) => {
      toast({
        title: "Plan creation failed",
        description: error.response?.data?.detail || "Failed to create plan",
        variant: "destructive",
      });
    },
  });

  const handleCalculatorSubmit = (e) => {
    e.preventDefault();
    calculateMutation.mutate(calculatorData);
  };

  const handleCreatePlan = () => {
    const planData = {
      ...calculatorData,
      name: `Nutrition Plan - ${new Date().toLocaleDateString()}`,
      notes: `Generated plan for ${calculatorData.age}y old ${calculatorData.gender}`,
    };
    createPlanMutation.mutate(planData);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCalculatorData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDiseaseChange = (diseaseId, checked) => {
    setCalculatorData(prev => ({
      ...prev,
      diseases: checked 
        ? [...prev.diseases, diseaseId]
        : prev.diseases.filter(id => id !== diseaseId)
    }));
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Nutrition Planning
        </h1>
        <p className="text-gray-600 mt-2">
          {isDoctor() ? 'Create personalized nutrition plans for your patients' : 'Calculate your nutritional needs and track your progress'}
        </p>
      </div>

      <div className="grid lg:grid-cols-2 gap-8">
        {/* Calorie Calculator */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calculator className="h-5 w-5" />
              Calorie Calculator
            </CardTitle>
            <CardDescription>
              Calculate daily caloric needs based on personal data and health conditions
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleCalculatorSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Age
                  </label>
                  <Input
                    name="age"
                    type="number"
                    value={calculatorData.age}
                    onChange={handleInputChange}
                    placeholder="25"
                    min="1"
                    max="120"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Gender
                  </label>
                  <select
                    name="gender"
                    value={calculatorData.gender}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                  >
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                  </select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Weight (kg)
                  </label>
                  <Input
                    name="weight"
                    type="number"
                    value={calculatorData.weight}
                    onChange={handleInputChange}
                    placeholder="70"
                    min="1"
                    step="0.1"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Height (cm)
                  </label>
                  <Input
                    name="height"
                    type="number"
                    value={calculatorData.height}
                    onChange={handleInputChange}
                    placeholder="175"
                    min="1"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Activity Level
                </label>
                <select
                  name="activity_level"
                  value={calculatorData.activity_level}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                >
                  <option value="sedentary">Sedentary (little/no exercise)</option>
                  <option value="light">Light (light exercise 1-3 days/week)</option>
                  <option value="moderate">Moderate (moderate exercise 3-5 days/week)</option>
                  <option value="active">Active (hard exercise 6-7 days/week)</option>
                  <option value="very_active">Very Active (very hard exercise, physical job)</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Goal
                </label>
                <select
                  name="goal"
                  value={calculatorData.goal}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                >
                  <option value="lose">Lose Weight</option>
                  <option value="maintain">Maintain Weight</option>
                  <option value="gain">Gain Weight</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Health Conditions
                </label>
                <div className="space-y-2 max-h-32 overflow-y-auto">
                  {diseases?.map((disease) => (
                    <label key={disease.id} className="flex items-center">
                      <input
                        type="checkbox"
                        checked={calculatorData.diseases.includes(disease.id)}
                        onChange={(e) => handleDiseaseChange(disease.id, e.target.checked)}
                        className="mr-2"
                      />
                      <span className="text-sm">{disease.name}</span>
                    </label>
                  ))}
                </div>
              </div>

              <div className="flex gap-2">
                <Button
                  type="submit"
                  className="flex-1"
                  disabled={calculateMutation.isPending}
                >
                  {calculateMutation.isPending ? 'Calculating...' : 'Calculate Calories'}
                </Button>

                {isDoctor() && (
                  <Button
                    type="button"
                    variant="outline"
                    onClick={handleCreatePlan}
                    disabled={createPlanMutation.isPending}
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Create Plan
                  </Button>
                )}
              </div>
            </form>

            {calculateMutation.data && (
              <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                <h4 className="font-medium text-blue-900 mb-2">Calculation Results:</h4>
                <div className="text-sm text-blue-800 space-y-1">
                  <div>BMR: {calculateMutation.data.bmr} calories</div>
                  <div>TDEE: {calculateMutation.data.tdee} calories</div>
                  <div className="font-semibold">
                    Recommended: {calculateMutation.data.recommended_calories} calories/day
                  </div>
                  {calculateMutation.data.disease_adjustments && (
                    <div className="mt-2">
                      <div className="font-medium">Disease Adjustments:</div>
                      {calculateMutation.data.disease_adjustments.map((adj, idx) => (
                        <div key={idx} className="ml-2">
                          {adj.disease}: {adj.adjustment > 0 ? '+' : ''}{adj.adjustment} calories
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Disease Demo & WhatsApp Integration */}
        <div className="space-y-6">
          {/* Disease Demo */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Heart className="h-5 w-5" />
                Disease-Calorie Demo
              </CardTitle>
              <CardDescription>
                See how different medical conditions affect caloric requirements
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Button
                onClick={() => setShowDemo(!showDemo)}
                variant="outline"
                className="w-full mb-4"
              >
                <Eye className="h-4 w-4 mr-2" />
                {showDemo ? 'Hide Demo' : 'Show Demo'}
              </Button>

              {showDemo && demoData && (
                <div className="space-y-3">
                  {demoData.disease_examples?.map((example, idx) => (
                    <div key={idx} className="p-3 border rounded-lg">
                      <div className="font-medium">{example.disease}</div>
                      <div className="text-sm text-gray-600 mb-1">
                        {example.description}
                      </div>
                      <div className="text-sm">
                        <span className="font-medium">Calorie Adjustment: </span>
                        <span className={example.adjustment > 0 ? 'text-green-600' : 'text-red-600'}>
                          {example.adjustment > 0 ? '+' : ''}{example.adjustment} calories
                        </span>
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        Restrictions: {example.dietary_restrictions}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* WhatsApp Integration */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MessageCircle className="h-5 w-5" />
                WhatsApp Integration
              </CardTitle>
              <CardDescription>
                Recent nutrition tips and reminders sent via WhatsApp
              </CardDescription>
            </CardHeader>
            <CardContent>
              {whatsappMessages?.length > 0 ? (
                <div className="space-y-3">
                  {whatsappMessages.slice(0, 3).map((message) => (
                    <div key={message.id} className="p-3 bg-green-50 rounded-lg">
                      <div className="text-sm font-medium text-green-800">
                        {message.message_type === 'tip' ? 'ðŸ’¡ Nutrition Tip' : 'â° Reminder'}
                      </div>
                      <div className="text-sm text-green-700 mt-1">
                        {message.content}
                      </div>
                      <div className="text-xs text-green-600 mt-2">
                        {new Date(message.created_at).toLocaleString()}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-6 text-gray-500">
                  <MessageCircle className="h-8 w-8 mx-auto mb-2 opacity-50" />
                  <p>No WhatsApp messages yet</p>
                  <p className="text-sm">Messages will appear here when sent</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Nutrition Plans */}
      <Card className="mt-8">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Activity className="h-5 w-5" />
            {isDoctor() ? 'Patient Plans' : 'My Nutrition Plans'}
          </CardTitle>
          <CardDescription>
            {isDoctor() ? 'Manage nutrition plans for your patients' : 'View and track your nutrition plans'}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {nutritionPlans?.length > 0 ? (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
              {nutritionPlans.map((plan) => (
                <div key={plan.id} className="p-4 border rounded-lg">
                  <h4 className="font-medium mb-2">
                    {plan.name || `Plan #${plan.id}`}
                  </h4>
                  <div className="text-sm text-gray-600 space-y-1">
                    <div>Age: {plan.age} years</div>
                    <div>Weight: {plan.weight} kg</div>
                    <div>Goal: {plan.goal}</div>
                    <div>Created: {new Date(plan.created_at).toLocaleDateString()}</div>
                  </div>
                  <Button size="sm" variant="outline" className="w-full mt-3">
                    <Eye className="h-3 w-3 mr-1" />
                    View Details
                  </Button>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <Activity className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p className="mb-2">No nutrition plans yet</p>
              <p className="text-sm">
                {isDoctor() ? 'Create plans for your patients using the calculator above' : 'Use the calculator to create your first nutrition plan'}
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default NutritionPlan;
